import React, { Fragment, useState } from "react";
import { Avatar } from '@material-ui/core';
import PageTitle from "../../../../layouts/PageTitle";
import { Dropdown, Table } from "react-bootstrap";
import { fire } from "../../../../../fire";
import { firestore } from "../../../../../fire";
import { collection, query, where } from "../../../../../fire"; 
import App from "../../../../../App";

export default class Recipe extends React.Component {

   state = {
      searchTerm : "",
      Users: []
   }
   constructor(props){
      super(props);   
   }
   searchByTerm = (value) => {
      this.setState({searchTerm : value});
   }
componentDidMount() {
     firestore.collection("Users").get().then((querySnapshot) => {
        let User = []
        querySnapshot.forEach((doc) => {
        console.log(`${doc.id} => ${doc.data().lastn}`);
        User.push({   
        id : doc.id,
        data: doc.data()})
        });
        this.setState({Users : User})
      });  
   }    

       delete = (id) => {
         console.log(id)
         firestore.collection("Users").doc(id).delete().then(() => {
           console.log("Document successfully deleted!");
           this.props.history.push("#")
         }).catch((error) => {console.error("Error removing document: ", error);
         });
      }



render() {



      return (
      <Fragment>  


        <div className="col-12">
        <div className="card">
        <div className="card-header">
        <h4 className="card-title">Customer List </h4>
            </div> 
            <div className="card-body">      
            <Table responsive className="w-100">
            <div id="example_wrapper" className="dataTables_wrapper">
            <table id="example" className="display w-100 dataTable">
            <thead>
                           <tr role="row">


                                    <th>Avatar</th>
                                    <th>Email</th>
                                    <th>Firstname</th>
                                    <th>Lastname</th>
                                    <th>PhoneNumber</th>

                                    {/* <th className="pl-5 width200">
                                    Billing Address
                                 </th> */}
                                 <th>Action</th>

                              </tr>


                              </thead>
                              <tbody>
                                 {this.state.Users.filter( val =>{
                                    // console.log(this.state.searchTerm);
                                    if (this.state.searchTerm === ""){

                                       return val;

                                    } else if (

                                       val.email.toLowerCase().includes(this.state.searchTerm.toLowerCase()) ||

                                       val.firstname.toLowerCase().includes(this.state.searchTerm.toLowerCase())
                                    )

                                    {   
                                       return val }
                                 }).map(data => {

                                    return (
                                       <tr>
                                          <td> <Avatar className ="rounded-circle img-fluid" src={data.data.avatar}/> </td>

                                          <td>{data.data.email}</td>
                                          <td>{data.data.firstname}</td>
                                          <td>{data.data.datalastname}</td>
                                          <td>{data.data.phonenumber}</td>

                                           <td>
                                                         <div
                                                         className="btn btn-danger shadow btn-xs sharp" onClick ={this.delete.bind(this, data.id)}
                                                      >
                                                         <i className="fa fa-trash"></i> </div></td>                
                                     </tr>
                                    );

                                 })}
                              </tbody>
                           </table>
                           </div>
                        </Table>
                     </div>
                  </div>
               </div>

         </Fragment>
      );
   };
};
